import React, { useState } from 'react';
import { Card } from 'primereact/card';
import { Button } from 'primereact/button';
import { RadioButton, RadioButtonChangeEvent } from 'primereact/radiobutton';

const Bills = () => {
  const [ingredient, setIngredient] = useState<string>('');
  const header = (
    <img alt="Card" src="https://primefaces.org/cdn/primereact/images/usercard.png" />
  );
  const footer = (
    <>
      <Button label="Save" icon="pi pi-check" />
      <Button label="Cancel" severity="secondary" icon="pi pi-times" style={{ marginLeft: '0.5em' }} />
    </>
  );

  return (
    <div style={{ backgroundColor: 'whitesmoke', flex: 1, padding: 80 }}>
      <div className="card flex justify-content-center">
        <Card title="Recharge or Pay Mobile Bill" subTitle="Card subtitle" footer={footer} className="md:w-25rem">
          <div className="flex flex-wrap gap-3">
            {['Cheese', 'Mushroom', 'Pepper', 'Onion'].map((item) => (
              <div className="flex align-items-center" key={item}>
                <RadioButton
                  inputId={`ingredient-${item}`}
                  name="pizza"
                  value={item}
                  onChange={(e: RadioButtonChangeEvent) => setIngredient(e.value)}
                  checked={ingredient === item}
                />
                <label htmlFor={`ingredient-${item}`} className="ml-2">{item}</label>
              </div>
            ))}
          </div>
          <p className="m-0">
            Lorem ipsum dolor sit amet, consectetur adipisicing elit. Inventore sed consequuntur error repudiandae 
            numquam deserunt quisquam repellat libero asperiores earum nam nobis, culpa ratione quam perferendis esse, cupiditate neque quas!
          </p>
        </Card>
      </div>
    </div>
  );
};

export default Bills;
